import React, { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext';
import api from '../services/api';
import { toast } from 'react-toastify';
import Navbar from '../components/Navbar';
import { QRCodeSVG } from 'qrcode.react';

const History = () => {
  const { user } = useAuth(); // Pastikan context mengembalikan user atau null
  const [tickets, setTickets] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedTicket, setSelectedTicket] = useState(null);

  useEffect(() => {
    // Cek apakah user sudah ada. Jika user null, jangan fetch dulu.
    if (user && user.id) {
      fetchHistory();
    }
  }, [user]); // <--- Tambahkan user ke dependency array

  const fetchHistory = async () => {
    try {
      setLoading(true); // Set loading true saat mulai fetch
      const response = await api.get(`/users/${user.id}/history`);
      setTickets(response.data.data || []);
    } catch (error) {
      console.error(error);
      // Jangan toast error jika user belum login/masalah auth
      if (user) toast.error('Gagal memuat riwayat pembelian');
    } finally {
      setLoading(false);
    }
  };

  const formatPrice = (price) => {
    return new Intl.NumberFormat('id-ID', {
      style: 'currency',
      currency: 'IDR',
      minimumFractionDigits: 0
    }).format(price);
  };

  // Tambahkan handling jika user belum login (Opsional, tergantung proteksi route)
  if (!user && loading) return <div>Loading User...</div>;

  return (
    <div className="min-h-screen bg-gray-50">
      <Navbar />

      <div className="container mx-auto px-4 py-12">
        <h1 className="text-3xl font-bold text-gray-800 mb-8">Riwayat Pembelian</h1>

        {loading ? (
          <div className="text-center py-12">Loading data...</div>
        ) : tickets.length > 0 ? (
          <div className="grid gap-6">
            {tickets.map((ticket) => (
              <div key={ticket.id} className="bg-white rounded-xl shadow-lg p-6">
                <div className="flex justify-between items-start">
                  <div className="flex-1">
                    <h3 className="text-xl font-semibold text-gray-800 mb-2">
                      {ticket.Event?.nama || 'Event Tidak Ditemukan'}
                    </h3>
                    <div className="space-y-1 text-gray-600">
                      <p><span className="font-semibold">Lokasi:</span> {ticket.Event?.lokasi || '-'}</p>
                      <p><span className="font-semibold">Tanggal Beli:</span> {new Date(ticket.waktu_beli).toLocaleDateString('id-ID')}</p>
                      <p><span className="font-semibold">Total:</span> {formatPrice(ticket.total_harga)}</p>
                      <p>
                        <span className="font-semibold">Status:</span>{' '}
                        <span className={`px-3 py-1 rounded-full text-sm ${ticket.status === 'aktif' ? 'bg-green-100 text-green-800' :
                          ticket.status === 'digunakan' ? 'bg-gray-100 text-gray-800' :
                            'bg-red-100 text-red-800'
                          }`}>
                          {ticket.status}
                        </span>
                      </p>
                    </div>
                  </div>

                  <button
                    onClick={() => setSelectedTicket(ticket)}
                    // Pastikan class 'bg-bbo-orange' sudah ada di tailwind config, jika tidak ganti ke 'bg-orange-500'
                    className="px-4 py-2 bg-gradient-to-r from-orange-500 to-red-500 text-white rounded-lg hover:shadow-lg transition"
                  >
                    Lihat QR
                  </button>
                </div>
              </div>
            ))}
          </div>
        ) : (
          <div className="text-center py-12 bg-white rounded-xl shadow-sm">
            <p className="text-gray-600 text-lg">Belum ada riwayat pembelian</p>
          </div>
        )}
      </div>

      {selectedTicket && (
        <div className="fixed inset-0 bg-black/50 flex items-center justify-center z-50 p-4" onClick={() => setSelectedTicket(null)}>
          <div className="bg-white rounded-xl p-8 max-w-md w-full animate-fade-in" onClick={(e) => e.stopPropagation()}>
            <h3 className="text-2xl font-bold text-gray-800 mb-4 text-center">
              E-Ticket
            </h3>
            <div className="text-center mb-6">
              {/* Ini akan generate ulang QR Code. Kalau mau ambil gambar dari DB ganti jadi: 
                  <img src={selectedTicket.qr_image} className="w-48 h-48 mx-auto" /> 
              */}
              <QRCodeSVG value={selectedTicket.kode_qr} size={200} className="mx-auto" />

              <p className="text-sm text-gray-600 mt-4 font-mono bg-gray-100 p-2 rounded">
                {selectedTicket.kode_qr}
              </p>
            </div>
            <button
              onClick={() => setSelectedTicket(null)}
              className="w-full py-2 bg-gray-200 text-gray-800 rounded-lg hover:bg-gray-300 font-semibold transition"
            >
              Tutup
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default History;
